var fs = require('fs')
var gr8 = require('src')

var css = gr8({
  spacing: [0, 1, 2, 3, 4],
  fontSize: [6.4, 3.2, 2.4, 1.6, 1.2, 1.0],
  lineHeight: [1, 1.5],
  size: [0, 100],
  viewport: 100,
  zIndex: [0, 1, 2, 3, 4],
  order: [0, 1, 2, 3, 4],
  opacity: [0, 25, 50, 75, 100],
  aspect: [0, 20, 50, 75, 100],
  textColumns: [1, 2, 3, 4],
  unit: 'rem',
  nested: false,
  responsive: false,
  attribute: true,
  max: true,
  breakpoints: {
    xl: '1439px',
    lg: '1260px',
    md: '1023px',
    sm: '767px'
  }
})

// process.stdout.write(css.toString())

fs.writeFile('dist/digdog.css', css, function (err) {
  if (err) {
    return console.log(err)
  }

  console.log('gr8 generated and saved to gr8.css')
})
